CREATE DATABASE car_rental;
USE car_rental;

CREATE TABLE `car-rental`.`klienci` (`ID_KLIENT` INT NULL , `IMIE` TEXT NOT NULL , `NAZWISKO` TEXT NOT NULL , `MIEJSCOWOSC` TEXT NOT NULL , `ULICA` TEXT NOT NULL , `NR_DOMU` CHAR(5) NOT NULL , `KOD_POCZT` CHAR(6) NOT NULL , `NR_TEL` TEXT NOT NULL , `EMAIL` TEXT NOT NULL , `NR_PRAWAJAZDY` CHAR(11) NOT NULL , `DATA_WAZNOSCI_PJ` DATE NOT NULL , `NR_KONTA` CHAR(26) NOT NULL , `NR_KARTY` CHAR(16) NOT NULL , PRIMARY KEY (`ID_KLIENT`)) ENGINE = InnoDB;car

CREATE TABLE `car-rental`.`samochody` (`ID_SAMOCHOD` INT NOT NULL AUTO_INCREMENT , `MARKA` TEXT NOT NULL , `MODEL` TEXT NOT NULL , `NR_REJ` VARCHAR(10) NOT NULL , `NR_DOWODU_REJ` VARCHAR(17) NOT NULL , `VIN` VARCHAR(17) NOT NULL , `ROK_PROD` YEAR NOT NULL , `TYP_PALIWA` ENUM('BENZYNA','DIESEL','ELEKTRYCZNY','HYBRYDA','INNE') NOT NULL , `PRZEBIEG` INT NOT NULL , `DATA_PRZEGL` DATE NOT NULL , `DATA_KOL_PRZEGL` DATE NOT NULL , `NAPED` ENUM('FWD','RWD','AWD','4X4','INNE') NOT NULL , `CENA` SMALLINT NOT NULL , `TYP_NADWOZIA` ENUM('COUPE','KABRIOLET','KOMBI','KOMPAKT','MINIVAN','SEDAN','VAN','SUV','INNE') NOT NULL , `KLASA` ENUM('ECO','STANDARD','PREMIUM','LUXURY','SPORT') NOT NULL , `ILOSC_MIEJSC` SMALLINT(1) NOT NULL , `POJE_BAGAZNIKA` SMALLINT NOT NULL , PRIMARY KEY (`ID_SAMOCHOD`)) ENGINE = InnoDB;

CREATE TABLE `car-rental`.`serwis` (`ID_SAMOCHOD` INT NOT NULL , `RODZAJ_SERWIS` TEXT NOT NULL , `DATA_SERWIS` DATE NOT NULL , `KOSZT_SERWIS` MEDIUMINT NOT NULL , `WYKONAWCA` TEXT NOT NULL , `OPIS_SERWIS` TEXT NOT NULL , PRIMARY KEY (`ID_SAMOCHOD`)) ENGINE = InnoDB;

CREATE TABLE `car-rental`.`ubezpieczenia` (`ID_SAMOCHOD` INT NOT NULL , `NUMER_POLISY` TEXT NOT NULL , `RODZAJ_UBEZP` ENUM('OC','AC') NOT NULL , `DATA_ROZPOCZ` DATE NOT NULL , `DATA_ZAKONCZ` DATE NOT NULL , `FIRMA_UBEZP` TEXT NOT NULL , `KOSZT_UBEZP` SMALLINT NOT NULL , `UWAGI` TEXT NOT NULL , PRIMARY KEY (`ID_SAMOCHOD`)) ENGINE = InnoDB;

CREATE TABLE `car-rental`.`wyposazenie` (`ID_SAMOCHOD` INT NOT NULL , `KLIMATYZACJA` BOOLEAN NOT NULL , `EKRAN_DOTYK` BOOLEAN NOT NULL , `RADIO` BOOLEAN NOT NULL , `GPS` BOOLEAN NOT NULL , `BLUETOOTH` BOOLEAN NOT NULL , `ANDROID_AUTO` BOOLEAN NOT NULL , `APPLE_CARPLAY` BOOLEAN NOT NULL , `PODGRZ_FOTELI` BOOLEAN NOT NULL , `PODGRZ_KIEROWNICY` BOOLEAN NOT NULL , `ELEKTR_SZYBY` BOOLEAN NOT NULL , `ELEKTR_LUST` BOOLEAN NOT NULL , `TEMPOMAT` BOOLEAN NOT NULL , `KAMERA_COFANIA` BOOLEAN NOT NULL , `HAK` BOOLEAN NOT NULL , `DACH_PANORAM` BOOLEAN NOT NULL , `ABS` BOOLEAN NOT NULL , `ESP` BOOLEAN NOT NULL , `ASR` BOOLEAN NOT NULL , `FCW` BOOLEAN NOT NULL , `AEB` BOOLEAN NOT NULL , `LKA` BOOLEAN NOT NULL , `SYS_ROZP_ZNAK` BOOLEAN NOT NULL , `BSM` BOOLEAN NOT NULL , `CZUJNIKI_PARK` BOOLEAN NOT NULL , `KAMERY_360` BOOLEAN NOT NULL , PRIMARY KEY (`ID_SAMOCHOD`)) ENGINE = InnoDB;

